//! this is the unoptimized version that performs all 100
//! passes, as per the original description of the problem

fn main() {
    // states for the 100 doors
    // uses a vector of booleans,
    // where state==false means the door is closed
    let mut doors = [false; 100];
    solve(&mut doors);

    for (idx, door) in doors.iter().enumerate() {
        println!("door {} open: {}", idx + 1, door);
    }
}

/// unoptimized solution for the 100 Doors problem,
/// performs all 100 passes and mutates the vector with
/// the states in place
fn solve(doors: &mut [bool]) {
    for pass in 1..101 {
        let mut p = pass;
        while p <= 100 {
            // flip the state of the door
            doors[p - 1] = !doors[p - 1];
            p += pass;
        }
    }
}

#[test]
fn solution() {
    let mut doors = [false; 100];
    solve(&mut doors);

    // test that the doors with index corresponding to
    // a perfect square are now open
    for i in 1..11 {
        assert!(doors[i * i - 1]);
    }
}
extern crate num;

use std::f64;
use std::iter::Map;
use std::ops::Range;

type DoorIter = Map<Range<u32>, fn(u32) -> DoorState>;

#[derive(Debug, PartialEq)]
enum DoorState {
    Open,
    Closed,
}

// This is an example of returning an iterator, this allows the caller to
// choose if they want to allocate or just process as a stream.
fn calculate_doors() -> DoorIter {
    fn door_status(door_number: u32) -> DoorState {
        let x = f64::from(door_number).sqrt();
        if (x - x.round()).abs() < f64::EPSILON {
            DoorState::Open
        } else {
            DoorState::Closed
        }
    }

    (1u32..101).map(door_status as fn(u32) -> DoorState)
}

fn main() {
    let doors = calculate_doors();
    for (i, x) in doors.enumerate() {
        println!("Door {} is {:?}", i + 1, x);
    }
}

#[test]
fn solution() {
    let doors = calculate_doors().collect::<Vec<DoorState>>();

    // test that the doors with index corresponding to
    // a perfect square are now open
    for i in 1..11 {
        assert_eq!(doors[i * i - 1], DoorState::Open);
    }
}

