fn main() {
    use ui_imp::Termion;

    let mut game = Game::new();
    let mut ui = Termion::new();

    loop {
        ui.draw(&game).unwrap();
        let key = ui.wait_key();
        if let Key::Quit = key {
            break;
        }
        game.step(key);
    }
}

