fn is_kprime(mut n: usize, k: usize) -> bool {
    let mut p = 2;
    let mut f = 0;

    while f < k && p * p <= n {
        while 0 == n % p {
            n /= p;
            f += 1;
        }
        p += 1;
    }

    f + (n > 1) as usize == k
}

fn get_kprimes(k: usize, amount: usize) -> Vec<usize> {
    (2..).filter(|&x| is_kprime(x, k)).take(amount).collect()
}

