fn fib(n: i64) -> Option<i64> {
    // A function declared inside another function does not pollute the outer namespace.
    fn actual_fib(n: i64) -> i64 {
        if n < 2 {
            n
        } else {
            actual_fib(n - 1) + actual_fib(n - 2)
        }
    }

    if n < 0 {
        None
    } else {
        Some(actual_fib(n))
    }
}

