// Dumbest iterative approach
fn big_pow(base: &BigInt, exp: BigInt) -> BigInt {
    if exp.is_negative() {
        panic!("Negative exponent won't compute!")
    }
    let mut tmp = base.clone();
    for _ in num::range(BigInt::from(1), exp) {
        tmp = tmp * base;
    }
    tmp
}

