fn median(samples: &[f64]) -> f64 {
    let mut xs = samples.to_vec();
    xs.sort_by(|x, y| x.partial_cmp(y).unwrap());

    let n = xs.len();
    if n % 2 == 0 {
        (xs[n / 2] + xs[(n / 2) + 1]) / 2.0
    } else {
        xs[n / 2]
    }
}

