/// The mean is not defined for an empty list, so we must return an Option
fn rms(list: &[f64]) -> Option<f64> {
    match list.len() {
        0 => None,
        n => {
            let sum = list.iter().fold(0., |a, &b| a + b * b);
            Some(f64::sqrt(sum / n as f64))
        }
    }
}

