extern crate crypto;

use crypto::digest::Digest;
use crypto::sha2::Sha256;

/// Decodes a base58-encoded string into an array of bytes.
fn decode_base58(address: &str) -> Result<Vec<u8>, &'static str> {
    const ALPHABET: &str = "123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz";
    const BASE: usize = 58;

    let mut result = vec![0; 25];

    for c in address.chars() {
        let mut value = match ALPHABET.find(c) {
            Some(index) => index,
            None => return Err("invalid character"),
        };

        for byte in &mut result {
            value += BASE * (*byte as usize);
            *byte = (value % 256) as u8;
            value /= 256;
        }

        if value > 0 {
            return Err("address too long");
        }
    }

    Ok(result.iter().rev().map(|&byte| byte as u8).collect())
}

/// Hashed the input with the SHA-256 algorithm twice, and returns the output.
fn double_sha256(bytes: &[u8]) -> Vec<u8> {
    let mut hasher = Sha256::new();

    hasher.input(bytes);
    let mut digest_1 = vec![0; 32];
    hasher.result(&mut digest_1);
    hasher.reset();

    hasher.input(&digest_1);
    let mut digest_2 = vec![0; 32];
    hasher.result(&mut digest_2);

    digest_2
}

/// Validates a bitcoin address.
///
/// Returns `Ok` if the address validates.
/// Returns `Err` with the reason if the address is invalid.
fn validate(address: &str) -> Result<(), &'static str> {
    let bytes = decode_base58(address)?;

    // A bitcoin address encodes 25 bytes:
    if bytes.len() != 25 {
        return Err("invalid length");
    }

    // The first byte is the version number, which will be zero for this task
    if bytes[0] != 0 {
        return Err("unknown version encountered");
    }

    // The next twenty bytes are a RIPEMD-160 digest, but you don't have to know that for this
    // task: you can consider them a pure arbitrary data
    let _ = &bytes[1..21];

    // The last four bytes are a checksum check. They are the first four bytes of a double SHA-256
    // digest of the previous 21 bytes.
    let checksum = &bytes[21..];
    let result = double_sha256(&bytes[..21]);
    if &result[..4] != checksum {
        return Err("checksum did not validate");
    }

    Ok(())
}

fn main() {
    use std::env;

    let result = match env::args().nth(2) {
        Some(address) => validate(&address),
        None => Err("no address supplied"),
    };

    match result {
        Ok(()) => println!("address is valid."),
        Err(reason) => println!("address is invalid: {}", reason),
    }
}

#[test]
fn test_valid() {
    assert!(validate("1AGNa15ZQXAZUgFiqJ2i7Z2DPU2J6hW62i").is_ok());
}

#[test]
fn test_invalid() {
    assert!(
        validate("1AGNa15ZQXAZUgFiqJ2i7Z2DPU2J6hW62j").unwrap_err() == "checksum did not validate"
    )
}

extern crate crypto;
extern crate hex;

use crypto::digest::Digest;
use crypto::ripemd160::Ripemd160;
use crypto::sha2::Sha256;
use hex::FromHex;

static X: &'static str = "50863AD64A87AE8A2FE83C1AF1A8403CB53F53E486D8511DAD8A04887E5B2352";
static Y: &'static str = "2CD470243453A299FA9E77237716103ABC11A1DF38855ED6F2EE187E9C582BA6";
static ALPHABET: [char; 58] = [
    '1', '2', '3', '4', '5', '6', '7', '8', '9', 'A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'J', 'K',
    'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'U', 'V', 'W', 'X', 'Y', 'Z', 'a', 'b', 'c', 'd', 'e',
    'f', 'g', 'h', 'i', 'j', 'k', 'm', 'n', 'o', 'p', 'q', 'r', 's', 't', 'u', 'v', 'w', 'x', 'y',
    'z',
];

fn base58_encode(bytes: &mut [u8]) -> String {
    let base = ALPHABET.len();
    if bytes.is_empty() {
        return String::from("");
    }
    let mut output: Vec<u8> = Vec::new();
    let mut num: usize;
    for _ in 0..33 {
        num = 0;
        for byte in bytes.iter_mut() {
            num = num * 256 + *byte as usize;
            *byte = (num / base) as u8;
            num %= base;
        }
        output.push(num as u8);
    }
    let mut string = String::new();
    for b in output.iter().rev() {
        string.push(ALPHABET[*b as usize]);
    }
    string
}

/// Hashes the input with the SHA-256 algorithm twice, and returns the output.
fn double_sha256(bytes: &[u8]) -> Vec<u8> {
    let mut hasher = Sha256::new();

    hasher.input(bytes);
    let mut digest_1 = vec![0; 32];
    hasher.result(&mut digest_1);
    hasher.reset();

    hasher.input(&digest_1);
    let mut digest_2 = vec![0; 32];
    hasher.result(&mut digest_2);

    digest_2
}

fn point_to_address(x: &str, y: &str) -> String {
    let mut addrv: Vec<u8> = Vec::with_capacity(65);
    addrv.push(4u8);
    addrv.append(&mut <Vec<u8>>::from_hex(x).unwrap());
    addrv.append(&mut <Vec<u8>>::from_hex(y).unwrap());
    // create Sha256 hasher
    let mut sha256 = Sha256::new();
    sha256.input(&addrv);
    let mut sha_digest = vec![0; 32];
    sha256.result(&mut sha_digest);
    // create Ripemd object
    let mut ripemd = Ripemd160::new();
    ripemd.input(&sha_digest);
    // prepend 0
    let mut ripemd_digest = vec![0; 21];
    ripemd.result(&mut ripemd_digest[1..]);
    // calculate checksum of extended ripemd digest
    let checksum = double_sha256(&ripemd_digest);
    ripemd_digest.extend_from_slice(&checksum[..4]);
    base58_encode(&mut ripemd_digest)
}

fn main() {
    println!("{}", point_to_address(X, Y));
}

