mod tests {
    use super::populate_tree;

    #[test]
    fn test_brownian_tree() {
        let width = 128;
        let height = 128;
        let mote_count = 1000;
        let mut field_raw = vec![0u8; width * height];
        populate_tree(&mut field_raw, width, height, mote_count);
        let our_max = field_raw.iter().max().unwrap();
        assert!(*our_max >= 1);
    }
}

