fn get_ips(host: &str) -> io::Result<impl Iterator<Item = IpAddr>> {
    let hosts = host.to_socket_addrs().unwrap();
    let ips = hosts.map(|h| match h {
        SocketAddr::V4(s_v4) => IpAddr::V4(*s_v4.ip()),
