    assert_eq!(cint_2 + cint_4, CustomInt { value: 6 });
    assert_eq!(cint_4 - cint_2, CustomInt { value: 2 });
    assert_eq!(cint_4 * cint_2, CustomInt { value: 8 });
    assert_eq!(cint_4 / cint_2, CustomInt { value: 2 });
