fn equilibrium_indices(v: &[i32]) -> Vec<usize> {
    let mut right = v.iter().fold(0, |a, &b| a + b);
    let mut left = i32::zero();

    v.iter().enumerate().fold(vec![], |mut out, (i, &el)| {
        right -= el;
        if left == right {
            out.push(i);
        }
        left += el;

        out
    })
}

