//! Rust uses condition variables (Condvars) for asynchronous event processing.  Each Mutex has a
//! list of zero or more Condvars, which are essentially events that the task may wait on or signal
//! when it holds the mutex.  When a task begins to wait on a condvar, it enters a waiting state
//! until the condvar is signaled by another task, at which point it resumes processing.  Thus,
//! each condvar represents an event on which a task may wait.  The one subtlety is that condvar
//! signals are only received if there is actually a task waiting on the signal--see the below
//! program for an example of how this may be achieved in practice.

extern crate time;

use std::sync::{Arc, Condvar, Mutex};
use std::thread::{self, spawn};
use std::time::Duration;

/// Given a duration to wait before sending an event from one process to another, returns the
/// elapsed time before the event was actually sent.
#[cfg_attr(feature = "cargo-clippy", allow(mutex_atomic))]
fn handle_event(duration: Duration) -> Duration {
    // Create a Mutex.  By default a Mutex is created with a single condition variable (condvar_id
    // 0) but it can be created with an arbitrary number using Mutex::new_with_condvars();
    let pair = Arc::new((Mutex::new(false), Condvar::new()));
    let pair_ = Arc::clone(&pair);
    let start = time::precise_time_ns();
    // Lock the mutex
    let &(ref mutex, ref cond) = &*pair;
    let mut guard = mutex.lock().unwrap();
    // Start our secondary task (which will signal our waiting main task)
    spawn(move || {
        let &(ref mutex_, ref cond_) = &*pair_;
        // Lock the mutex
        let mut guard = mutex_.lock().unwrap();
        *guard = true;

        // Sleep for `duration`.
        thread::sleep(duration);
        // Signal the waiting mutex (equivalent to guard.cond.signal_on(0)).
        // One can also signal to all tasks on the waiting mutex with broadcast (broadcast_on(0)).
        //
        // Note that if no tasks are actually waiting yet (which is possible, if we got the lock
        // before the other task), then this might report that it failed to wake up any tasks.
        // That is why the mutex was locked before the task was spawned--we know we cannot possibly
        // get past the mutex at the top of the task until the wait() statement below is reached.
        cond_.notify_one();
        // Although we signaled the waiting mutex, it will not awaken until this guard is dropped.
    });
    // Wait for the event state to be set to signaled (equivalent to guard.cond.wait_on(0)).
    while !*guard {
        guard = cond.wait(guard).unwrap();
    }
    // Should be done signaling (i.e. we've waited for `duration`).
    let end = time::precise_time_ns();
    // When the guard exits scope, the condvar is reset.
    drop(guard);
    // Return the elapsed time
    Duration::from_millis((end - start) / 1_000_000)
}

pub fn main() {
    let duration = Duration::from_secs(1); // Process event after one second.
    println!(
        "{} seconds elapsed before event triggered",
        handle_event(duration).as_secs()
    );
}

#[test]
pub fn test_events() {
    // Process event after one tenth of a second.
    let duration = Duration::from_secs(1) / 10;

    // Make sure it really did take at least that long for the event to be processed.
    let out = handle_event(duration);
    assert!(duration <= out);
}

//! Author : Thibault Barbie
//!
//! A simple evolutionary algorithm written in Rust.

extern crate rand;

use rand::distributions::Uniform;
use rand::prelude::*;

fn main() {
    let target = "METHINKS IT IS LIKE A WEASEL";
    let copies = 100;
    let mutation_rate = 1.0 / 20.0; // = 0.05 = 5%

    let mut rng = SmallRng::from_entropy();

    // Generate first sentence, mutating each character
    let start = mutate(&mut rng, target, 1.0); // = 100%

    println!("{}", target);
    println!("{}", start);

    evolve(&mut rng, target, start, copies, mutation_rate);
}

/// Evolution algorithm
///
/// Evolves `parent` to match `target`.  Returns the number of evolutions performed.
fn evolve<R: Rng>(
    rng: &mut R,
    target: &str,
    mut parent: String,
    copies: usize,
    mutation_rate: f64,
) -> usize {
    let mut counter = 0;
    let mut parent_fitness = target.len() + 1;

    loop {
        counter += 1;

        let (best_fitness, best_sentence) = (0..copies)
            .map(|_| {
                // Copy and mutate a new sentence.
                let sentence = mutate(rng, &parent, mutation_rate);
                // Find the fitness of the new mutation
                (fitness(target, &sentence), sentence)
            })
            .min_by_key(|&(f, _)| f) // find the closest mutation to the target
            .unwrap(); // fails if `copies == 0`

        // If the best mutation of this generation is better than `parent` then "the fittest
        // survives" and the next parent becomes the best of this generation.
        if best_fitness < parent_fitness {
            parent = best_sentence;
            parent_fitness = best_fitness;
            println!(
                "{} : generation {} with fitness {}",
                parent, counter, best_fitness
            );

            if best_fitness == 0 {
                return counter;
            }
        }
    }
}

/// Computes the fitness of a sentence against a target string, returning the number of
/// incorrect characters.
fn fitness(target: &str, sentence: &str) -> usize {
    sentence
        .chars()
        .zip(target.chars())
        .filter(|&(c1, c2)| c1 != c2)
        .count()
}

/// Mutation algorithm.
///
/// It mutates each character of a string, according to a `mutation_rate`.
fn mutate<R: Rng>(rng: &mut R, sentence: &str, mutation_rate: f64) -> String {
    let maybe_mutate = |c| {
        if rng.gen_bool(mutation_rate) {
            random_char(rng)
        } else {
            c
        }
    };
    sentence.chars().map(maybe_mutate).collect()
}

/// Generates a random letter or space.
fn random_char<R: Rng>(rng: &mut R) -> char {
    // Something similar to `rand::distributions::Alphanumeric` might be faster.

    // Returns a value in the range [A, Z] + an extra slot for the space character.
    // `Uniform` rather than `gen_range`'s `Uniform::sample_single` for speed
    let range = Uniform::new_inclusive(b'A', b'Z' + 1);
    match rng.sample(range) {
        c if c == b'Z' + 1 => ' ', // the `char` after 'Z'
        c => c as char,
    }
}

use std::collections::HashMap;
use std::env;
use std::fs::File;
use std::io::prelude::*;
use std::io::stdin;
use std::num::Wrapping;

fn main() {
    let args: Vec<_> = env::args().collect();
    if args.len() < 2 {
        println!("Usage: {} [path] (--debug)", args[0]);
        return;
    }

    let src: Vec<char> = {
        let mut buf = String::new();
        match File::open(&args[1]) {
            Ok(mut f) => {
                f.read_to_string(&mut buf).unwrap();
            }
            Err(e) => {
                println!("Error opening '{}': {}", args[1], e);
                return;
            }
        }

        buf.chars().collect()
    };

    // Launch options
    let debug = args.contains(&"--debug".to_owned());

    // One pass to find bracket pairs.
    let brackets: HashMap<usize, usize> = {
        let mut m = HashMap::new();
        let mut scope_stack = Vec::new();
        for (idx, ch) in src.iter().enumerate() {
            match *ch {
                '[' => {
                    scope_stack.push(idx);
                }
                ']' => {
                    m.insert(scope_stack.pop().unwrap(), idx);
                }
                _ => (),
            }
        }

        m
    };

    // Program counter
    let mut pc: usize = 0;

    // Program memory
    let mut mem: [Wrapping<u8>; 5000] = [Wrapping(0); 5000];

    // Pointer
    let mut ptr: usize = 0;

    // Bracket stack
    let mut stack: Vec<usize> = Vec::new();

    let stdin_ = stdin();
    let mut reader = stdin_.lock().bytes();
    while pc < src.len() {
        let Wrapping(val) = mem[ptr];

        if debug {
            println!(
                "(BFDB) PC: {:04} \tPTR: {:04} \t$PTR: {:03} \tSTACK_DEPTH: {} \tSYMBOL: {}",
                pc,
                ptr,
                val,
                stack.len(),
                src[pc]
            );
        }

        const ONE: Wrapping<u8> = Wrapping(1);
        match src[pc] {
            '>' => {
                ptr += 1;
            }
            '<' => {
                ptr -= 1;
            }
            '+' => {
                mem[ptr] += ONE;
            }
            '-' => {
                mem[ptr] -= ONE;
            }
            '[' => {
                if val == 0 {
                    pc = brackets[&pc];
                } else {
                    stack.push(pc);
                }
            }
            ']' => {
                let matching_bracket = stack.pop().unwrap();
                if val != 0 {
                    pc = matching_bracket - 1;
                }
            }
            '.' => {
                if debug {
                    println!("(BFDB) STDOUT: '{}'", val as char); // Intercept output
                } else {
                    print!("{}", val as char);
                }
            }
            ',' => {
                mem[ptr] = Wrapping(reader.next().unwrap().unwrap());
            }
            _ => (),
        }

        pc += 1;
    }
}

