fn write_file(filename: &str) -> impl Fn(&mut Read) -> Result<()> {
    let filename = filename.to_string();
    move |stream| {
        let mut file = File::create(&filename).unwrap();
        let mut buf = [0; 2048];
        loop {
            match stream.read(&mut buf) {
                Ok(0) => break,
                Ok(n) => file.write_all(&buf[0..n]).unwrap(),
                Err(err) => panic!(err),
            };
        }
        Ok(())
    }
}

