// For random generation
extern crate rand;

// For fmt::Display
use std::fmt;
// For I/O (stdin, stdout, etc)
use std::io::prelude::*;

use rand::Rng;

/// A simple struct for a board
struct Board {
    /// The cells of the board
    cells: Vec<bool>,
    /// The size of the board
    size: usize,
}

// Functions for the Board struct
impl Board {
    /// Generate a new, empty board, of size >= 1
    ///
    /// Returns a Board in the "off" state, where all cells are 0.
    /// If a size of 0 is given, a Board of size 1 will be created instead.
    /// A mutable board is required for using Board::fliprow and Board::flipcol functions.
    ///
    /// ```
    /// let mut board: Board = Board::new(3);
    /// ```
    fn new(size: usize) -> Board {
        // Ensure we make a board with a non-zero size
        if size > 0 {
            Board {
                cells: vec![false; size * size],
                size,
            }
        } else {
            Board::new(1)
        }
    }

    /// Flip the specified row
    ///
    /// Returns true if the row is within the size, false otherwise.
    ///
    /// ```
    /// let mut board: Board = Board::new(3);
    /// board.fliprow(1);
    /// ```
    fn fliprow(&mut self, row: usize) -> bool {
        // Check constraints
        if row > self.size {
            return false;
        }
        // Starting position in the vector
        let start = row * self.size;
        // Loop through the vector row
        for i in start..start + self.size {
            self.cells[i] = !self.cells[i];
        }
        true
    }

    /// Flip the specified column
    ///
    /// Returns true if the column is within the size, false otherwise.
    ///
    /// ```
    /// let mut board: Board = Board::new(3);
    /// board.flipcol(0);
    /// ```
    fn flipcol(&mut self, col: usize) -> bool {
        // Check constraints
        if col > self.size {
            return false;
        }
        // Loop through the vector column
        for i in 0..self.size {
            self.cells[col + i * self.size] = !self.cells[col + i * self.size];
        }
        true
    }

    /// Generate a random board
    ///
    /// Returns a Board in a random state.
    /// If a size of 0 is given, a Board of size 1 will be created instead.
    ///
    /// ```
    /// let target: Board = Board::random(3);
    /// ```
    fn random<R: Rng>(rng: &mut R, size: usize) -> Board {
        // Ensure we make a board with a non-zero size
        if size == 0 {
            return Board::random(rng, 1);
        }

        // Make a vector of the board size with random bits
        let cells = (0..size * size)
            .map(|_| rng.gen::<bool>())
            .collect::<Vec<_>>();
        // Return the random board
        Board { cells, size }
    }
}

impl PartialEq for Board {
    fn eq(&self, rhs: &Board) -> bool {
        self.cells == rhs.cells
    }
}

// Implement the Display format, used with `print!("{}", &board);`
impl fmt::Display for Board {
    // Example output:
    //   0 1 2
    // 0 0 1 0
    // 1 1 0 0
    // 2 0 1 1
    fn fmt(&self, f: &mut fmt::Formatter) -> fmt::Result {
        // Get the string width of the size of the board
        let width = (self.size - 1).to_string().len();
        // Write the initial spaces (upper left)
        write!(f, "{space: >0$}", width, space = " ")?;
        // Write the column numbers
        for i in 0..self.size {
            write!(f, " {offset:>0$}", width, offset = i)?;
        }
        // Newline for rows
        writeln!(f)?;
        // Loop through the rows
        for row in 0..self.size {
            // Write the row number
            write!(f, "{row:>0$}", width, row = row)?;
            // Loop through the columns
            for col in 0..self.size {
                // Get the value of the cell as 1 or 0
                let p = self.cells[row * self.size + col] as usize;
                // Write the column value
                write!(f, " {col:>0$}", width, col = p)?;
            }
            // Newline for next row
            writeln!(f)?;
        }
        // Return Formatter result
        Ok(())
    }
}

fn main() {
    let mut rng = rand::thread_rng();

    // The board size
    let size: usize = 3;
    // The target board
    let target: Board = Board::random(&mut rng, size);
    // The user board
    let mut board: Board = Board::new(size);
    // How many moves taken
    let mut moves: u32 = 0;
    // Loop until win or quit
    'mainloop: loop {
        // User input
        let mut input: String;
        // Write the boards
        println!("Target:\n{}\nBoard:\n{}", &target, &board);
        // User input loop
        'userinput: loop {
            // Prompt
            print!("\nFlip? [q|[r|c]#] ");
            // Flush stdout to write the previous print, if we can't then exit
            match std::io::stdout().flush() {
                Ok(_) => {}
                Err(e) => {
                    println!("Error: cannot flush stdout: {}", e);
                    break 'mainloop;
                }
            };
            // Reset input for each loop
            input = String::new();
            // Read user input
            match std::io::stdin().read_line(&mut input) {
                Ok(_) => {
                    input = input.trim().to_string();
                    // Get the first character
                    let rc: char = match input.chars().nth(0) {
                        Some(c) => c,
                        None => {
                            println!("Error: No input");
                            continue 'userinput;
                        }
                    };
                    // Make sure input is r, c, or q
                    if rc != 'r' && rc != 'c' && rc != 'q' {
                        println!("Error: '{}': Must use 'r'ow or 'c'olumn or 'q'uit", rc);
                        continue 'userinput;
                    }
                    // If input is q, exit game
                    if rc == 'q' {
                        println!("Thanks for playing!");
                        break 'mainloop;
                    }
                    // If input is r or c, get the number after
                    let n: usize = match input[1..].to_string().parse() {
                        Ok(x) => {
                            // If we're within bounds, return the parsed number
                            if x < size {
                                x
                            } else {
                                println!(
                                    "Error: Must specify a row or column within size({})",
                                    size
                                );
                                continue 'userinput;
                            }
                        }
                        Err(_) => {
                            println!(
                                "Error: '{}': Unable to parse row or column number",
                                input[1..].to_string()
                            );
                            continue 'userinput;
                        }
                    };
                    // Flip the row or column specified
                    match rc {
                        'r' => board.fliprow(n),
                        'c' => board.flipcol(n),
                        _ => {
                            // We want to panic here because should NEVER
                            // have anything other than 'r' or 'c' here
                            panic!("How did you end up here?");
                        }
                    };
                    // Increment moves
                    moves += 1;
                    println!("Moves taken: {}", moves);
                    break 'userinput;
                }
                Err(e) => {
                    println!("Error reading input: {}", e);
                    break 'mainloop;
                }
            }
        } // 'userinput
        if board == target {
            println!("You win!");
            break;
        }
    } // 'mainloop
}

use std::cmp;
use std::collections::BTreeMap;
use std::i32;
use std::ops::{Index, IndexMut};

/// A 2-D array for storing data per-vertex pair. 1-indexed.
#[derive(Debug, Default)]
struct VertexArray<T> {
    dimension: usize,
    data: Vec<T>,
}

impl<T> VertexArray<T>
where
    T: Default + Copy + Clone,
{
    fn new(dimension: usize) -> Self {
        Self {
            dimension,
            data: vec![T::default(); dimension * dimension],
        }
    }
}

impl<T> Index<(usize, usize)> for VertexArray<T> {
    type Output = T;

    fn index(&self, (i, j): (usize, usize)) -> &Self::Output {
        let (i, j) = (i - 1, j - 1);
        &self.data[self.dimension * i + j]
    }
}

impl<T> IndexMut<(usize, usize)> for VertexArray<T> {
    fn index_mut(&mut self, (i, j): (usize, usize)) -> &mut Self::Output {
        let (i, j) = (i - 1, j - 1);
        &mut self.data[self.dimension * i + j]
    }
}

/// Returns a `BTreeMap` mapping pairs of vertices to a pair consisting of the weight of the
/// shortest path between them, and a `Vec` containing the path itself.
fn floyd_warshall(graph: &[((usize, usize), i32)]) -> BTreeMap<(usize, usize), (i32, Vec<usize>)> {
    let num_vertices = graph
        .iter()
        .map(|&((u, v), _)| cmp::max(u, v))
        .max()
        .unwrap();

    let mut dist = VertexArray::<Option<i32>>::new(num_vertices);
    let mut next = VertexArray::<Option<usize>>::new(num_vertices);

    for &((u, v), w) in graph {
        dist[(u, v)] = Some(w);
        next[(u, v)] = Some(v);
    }

    for k in 1..=num_vertices {
        dist[(k, k)] = Some(0);
    }

    for k in 1..=num_vertices {
        for i in 1..=num_vertices {
            for j in 1..=num_vertices {
                if let (Some(w1), Some(w2)) = (dist[(i, k)], dist[(k, j)]) {
                    if dist[(i, j)].is_none() || dist[(i, j)].unwrap() > w1 + w2 {
                        dist[(i, j)] = Some(w1 + w2);
                        next[(i, j)] = next[(i, k)];
                    }
                }
            }
        }
    }

    let mut pairs = BTreeMap::new();

    for i in 1..=num_vertices {
        for j in 1..=num_vertices {
            if i == j {
                continue;
            }

            let distance = dist[(i, j)].unwrap();
            let path = path(&next, (i, j));
            pairs.insert((i, j), (distance, path));
        }
    }

    pairs
}

fn path(next: &VertexArray<Option<usize>>, (mut u, v): (usize, usize)) -> Vec<usize> {
    let mut path = vec![u];

    while u != v && next[(u, v)].is_some() {
        u = next[(u, v)].unwrap();
        path.push(u);
    }

    path
}

fn main() {
    let weights = vec![
        ((1, 3), -2),
        ((2, 1), 4),
        ((2, 3), 3),
        ((3, 4), 2),
        ((4, 2), -1),
    ];

    println!("{:<7} {:<7} {}", "pair", "dist", "path");
    let pairs = floyd_warshall(&weights);
    for (pair, &(distance, ref path)) in &pairs {
        println!(
            "{:<7} {:<7} {:?}",
            format!("{} -> {}", pair.0, pair.1),
            format!("{:>4}", distance),
            path
        );
    }
}

