fn main() {
    // opens file for writing replaced lines
    let out_fd = OpenOptions::new()
        .write(true)
        .create(true)
        .open("resources/output.txt");

    // defining a closure write_line
    let write_line = |line: &str| match out_fd {
        Ok(ref v) => {
            let mut writer = BufWriter::new(v);
            writer.write_all(line.as_bytes()).unwrap();
        }
        Err(ref e) => {
            println!("Error:{}", e);
        }
    };
    // read input file
    match File::open("resources/paragraph.txt") {
        Ok(handle) => {
            let mut reader = BufReader::new(handle);
            let mut line = String::new();
            // read the first line
            reader.read_line(&mut line).unwrap();
            // loop until line end
            while line.trim() != "" {
                let mut replaced_line = line.trim().replace("Goodbye London!", "Hello New York!");
                replaced_line += "\n";
                write_line(&replaced_line[..]);
                line.clear();
                reader.read_line(&mut line).unwrap();
            }
        }
        Err(e) => println!("Error:{}", e),
    }
}

/// Encode an usize
fn gray_encode(integer: usize) -> usize {
    (integer >> 1) ^ integer
}

/// Decode an usize
fn gray_decode(integer: usize) -> usize {
    match integer {
        0 => 0,
        _ => integer ^ gray_decode(integer >> 1),
    }
}

fn main() {
    for i in 0..32 {
        println!(
            "{:2} {:0>5} {:0>5} {:2}",
            i,
            i,
            gray_encode(i),
            gray_decode(i)
        );
    }
}

#[test]
fn test_coherence() {
    assert!((0..1000).all(|x| gray_decode(gray_encode(x)) == x));
}

