    assert_eq!(&seq[0..4], [27, 82, 41, 124]);
    assert_eq!(&seq[seq.len() - 4..], [8, 4, 2, 1]);
}

#[test]
fn test_biggest() {
    let (biggest, length) = biggest_hailstone(100_000);
    assert_eq!(biggest, 77031);
    assert_eq!(length, 351);
}

