    // Test that the first ten values are as expected
    // The first two values are hardcoded, so no need to test those.
    let hofstadter_q_expected = vec![2, 3, 3, 4, 5, 5, 6, 6, 6, 8];
    assert_eq!(hofstadter_q_expected, hofstadter_q);
