use std::fs::File;
use std::io::{BufRead, BufReader, BufWriter, Read, Write};
use std::iter::repeat;

/// Simple 8-bit grayscale image
struct ImageGray8 {
    width: usize,
    height: usize,
    data: Vec<u8>,
}

fn load_pgm(filename: &str) -> ImageGray8 {
    // Open file
    let mut file = BufReader::new(File::open(filename).unwrap());

    // Read header
    let mut magic_in = String::new();
    let _ = file.read_line(&mut magic_in).unwrap();
    let mut width_in = String::new();
    let _ = file.read_line(&mut width_in).unwrap();
    let mut height_in = String::new();
    let _ = file.read_line(&mut height_in).unwrap();
    let mut maxval_in = String::new();
    let _ = file.read_line(&mut maxval_in).unwrap();

    assert_eq!(magic_in, "P5\n");
    assert_eq!(maxval_in, "255\n");

    // Parse header

    let width = width_in.trim().parse::<usize>().unwrap();
    let height: usize = height_in.trim().parse::<usize>().unwrap();

    println!("Reading pgm file {}: {} x {}", filename, width, height);

    // Create image and allocate buffer

    let mut img = ImageGray8 {
        width: width,
        height: height,
        data: repeat(0u8).take(width * height).collect(),
    };

    // Read image data
    let len = img.data.len();
    match file.read(&mut img.data) {
        Ok(bytes_read) if bytes_read == len => println!("Read {} bytes", bytes_read),
        Ok(bytes_read) => println!("Error: read {} bytes, expected {}", bytes_read, len),
        Err(e) => println!("error reading: {}", e),
    }

    img
}

fn save_pgm(img: &ImageGray8, filename: &str) {
    // Open file
    let mut file = BufWriter::new(File::create(filename).unwrap());

    // Write header
    if let Err(e) = writeln!(&mut file, "P5\n{}\n{}\n255", img.width, img.height) {
        println!("Failed to write header: {}", e);
    }

    println!(
        "Writing pgm file {}: {} x {}",
        filename, img.width, img.height
    );

    // Write binary image data
    if let Err(e) = file.write_all(&(img.data[..])) {
        println!("Failed to image data: {}", e);
    }
}

fn hough(image: &ImageGray8, out_width: usize, out_height: usize) -> ImageGray8 {
    let in_width = image.width;
    let in_height = image.height;

    // Allocate accumulation buffer
    let out_height = ((out_height / 2) * 2) as usize;
    let mut accum = ImageGray8 {
        width: out_width,
        height: out_height,
        data: repeat(255).take(out_width * out_height).collect(),
    };

    // Transform extents
    let rmax = (in_width as f64).hypot(in_height as f64);
    let dr = rmax / (out_height / 2) as f64;
    let dth = std::f64::consts::PI / out_width as f64;

    // Process input image in raster order
    for y in 0..in_height {
        for x in 0..in_width {
            let in_idx = y * in_width + x;
            let col = image.data[in_idx];
            if col == 255 {
                continue;
            }

            // Project into rho,theta space
            for jtx in 0..out_width {
                let th = dth * (jtx as f64);
                let r = (x as f64) * (th.cos()) + (y as f64) * (th.sin());

                let iry = out_height / 2 - (r / (dr as f64) + 0.5).floor() as usize;
                let out_idx = jtx + iry * out_width;
                let col = accum.data[out_idx];
                if col > 0 {
                    accum.data[out_idx] = col - 1;
                }
            }
        }
    }
    accum
}

fn main() {
    let image = load_pgm("resources/Pentagon.pgm");

    let accum = hough(&image, 460, 360);

    save_pgm(&accum, "hough.pgm");
}

// Implement data structures for a Huffman encoding tree:
use std::cmp::Ordering;
use std::cmp::Ordering::{Equal, Greater, Less};
use std::collections::hash_map::Entry::{Occupied, Vacant};
use std::collections::BinaryHeap;
use std::collections::HashMap;

/// Each `HNode` has a weight, representing the sum of the frequencies for all its
/// children. It is either a leaf (containing a character), or an `HTree` (containing two children)
struct HNode {
    weight: usize,
    item: HItem,
}

enum HItem {
    Tree(HTreeData),
    Leaf(char),
}

struct HTreeData {
    left: Box<HNode>,
    right: Box<HNode>,
}

/// Implementing comparison traits (`Ord` and all its dependencies) such that the `HNode` with the
/// greatest weight is the smallest in a comparison. Basically reversing all the comparison
/// operators.
impl Ord for HNode {
    fn cmp(&self, other: &HNode) -> Ordering {
        match self.weight.cmp(&other.weight) {
            Less => Greater,
            Equal => Equal,
            Greater => Less,
        }
    }
}

impl PartialOrd for HNode {
    fn partial_cmp(&self, other: &HNode) -> Option<Ordering> {
        Some(self.cmp(other))
    }
}

impl Eq for HNode {}
impl PartialEq for HNode {
    fn eq(&self, other: &HNode) -> bool {
        self.weight == other.weight
    }
}

/// Takes a non-empty string (function will fail if string is empty) and computes the Huffman
/// encoding tree for that string.
fn huffman_tree(input: &str) -> HNode {
    // 1. Loop through all the characters in that string, adding them to a HashMap
    //    of character to frequency.
    let mut freq = HashMap::new();
    for ch in input.chars() {
        match freq.entry(ch) {
            Vacant(entry) => {
                entry.insert(1);
            }
            Occupied(mut entry) => {
                *entry.get_mut() += 1;
            }
        };
    }

    // 2. For each (character, frequency) pair in the HashMap, add a Leaf to a
    //    PriorityQueue
    let mut queue = BinaryHeap::<HNode>::new();
    for (ch, freq) in &freq {
        let new_node = HNode {
            weight: *freq,
            item: HItem::Leaf(*ch),
        };
        queue.push(new_node);
    }

    // 3. Pop two items with the least weight from the queue, combine them into
    //    a tree as children. The parent node's weight is the sum of the
    //    children's weight. Continue until one item is left on the queue, and
    //    return that item.
    while queue.len() > 1 {
        let item1 = queue.pop().unwrap();
        let item2 = queue.pop().unwrap();
        let new_node = HNode {
            weight: item1.weight + item2.weight,
            item: HItem::Tree(HTreeData {
                left: Box::new(item1),
                right: Box::new(item2),
            }),
        };
        queue.push(new_node);
    }
    queue.pop().unwrap()
}

/// Takes a Huffman Tree, traverse it and build a table with each character and
/// its encoding string.
fn build_encoding_table(tree: &HNode, table: &mut HashMap<char, String>, start_str: &str) {
    match tree.item {
        HItem::Tree(ref data) => {
            build_encoding_table(&data.left, table, &format!("{}0", start_str)[..]);
            build_encoding_table(&data.right, table, &format!("{}1", start_str)[..]);
        }
        HItem::Leaf(ch) => {
            table.insert(ch, start_str.to_string());
        }
    };
}

/// Attempts to construct a tree, and test that the construction is successful
///
/// ```
///     7
///    ----
///   /    \
///  4:'4'  3
///       -----
///      /     \
///     2:'2'  1:'1'
/// ```
#[test]
fn test_tree_construction() {
    let to_encode = "4444221";
    let tree = huffman_tree(to_encode);
    assert!(tree.weight == 7);
    let children = match tree.item {
        HItem::Tree(data) => data,
        HItem::Leaf(_) => panic!("Tree Missing Children!"),
    };
    let left = &children.left;
    let right = &children.right;
    assert!(right.weight == 4);
    assert!(left.weight == 3);
    let right_char = match right.item {
        HItem::Tree(_) => panic!("Node is not Leaf Node!"),
        HItem::Leaf(ch) => ch,
    };
    assert!(right_char == '4');
    let children = match left.item {
        HItem::Tree(ref data) => data,
        HItem::Leaf(_) => panic!("Tree Missing Children!"),
    };
    let left = &children.left;
    let right = &children.right;
    let left_char = match left.item {
        HItem::Tree(_) => panic!("Node is not Leaf Node!"),
        HItem::Leaf(ch) => ch,
    };
    let right_char = match right.item {
        HItem::Tree(_) => panic!("Node is not Leaf Node!"),
        HItem::Leaf(ch) => ch,
    };
    match (left.weight, right.weight) {
        (1, 2) => {
            assert!(left_char == '1');
            assert!(right_char == '2');
        }
        (2, 1) => {
            assert!(left_char == '2');
            assert!(right_char == '1');
        }
        (_, _) => {
            panic!("Incorrect Leaf Nodes");
        }
    };
}

/// Constructs a table:
///
/// ```
///  '4': 1
///  '2': 01 OR 00
///  '1': 00    01
///  ```
///
/// And tests that the table was correctly constructed
#[test]
fn test_table_construction() {
    let to_encode = "4444221";
    let tree = huffman_tree(to_encode);
    let mut table = HashMap::<char, String>::new();
    build_encoding_table(&tree, &mut table, "");
    let one = &*table[&'1'];
    let two = &*table[&'2'];
    let four = &*table[&'4'];
    assert!(four == "1");
    assert!((one == "01" && two == "00") || (one == "00" && two == "01"));
}

fn main() {
    let to_encode = "this is an example for huffman encoding";
    let tree = huffman_tree(to_encode);
    let mut table = HashMap::<char, String>::new();
    build_encoding_table(&tree, &mut table, "");

    for (ch, encoding) in &table {
        println!("{}: {}", *ch, encoding);
    }
}

