    #[test]
    fn sorted() {
        let sorted = [1, 2, 3, 4, 5, 6, 7, 8, 9];
        assert!(jortsort(&sorted))
    }

    #[test]
    fn unsorted() {
        let unsorted = [1, 3, 2];
        assert!(!jortsort(&unsorted))
    }
}

