    #[test]
    fn test_lloyd2d() {
        let mut rng = unseeded_stdrng();
        let points = generate_points(1000, &mut rng);

        let (clusters, stats) = lloyd(&points, 4, 0.001, 100, &mut rng);

        assert!(clusters.len() == 4);
        for i in 0..clusters.len() {
