fn max_path(vector: &mut Vec<Vec<u32>>) -> u32 {
    while vector.len() > 1 {
        let last = vector.pop().unwrap();
        let ante = vector.pop().unwrap();

        let mut new: Vec<u32> = Vec::new();

        for (i, value) in ante.iter().enumerate() {
            new.push(cmp::max(last[i], last[i + 1]) + value);
        }

        vector.push(new);
    }

    vector[0][0]
}

