const EPSILON: f64 = 1e-10;

fn nth_root(x: f64, n: f64) -> f64 {
    let mut x0: f64 = x;
    loop {
        let delta = (x / x0.powf(n - 1.0) - x0) / n;
        x0 += delta;
        if delta.abs() < EPSILON {
            return x0;
        }
    }
}

fn main() {
    println!("{}", nth_root(8.0, 3.0));
    println!("{}", nth_root(4.0, 2.0));
    println!("{}", nth_root(169.0, 2.0));
    println!("{}", nth_root(81.0, 4.0));
}

