//! This example uses the `glium` library, a high level, safe wrapper for OpenGL.
//!
//! It is based off the example in the [official tutorial].
//!
//! [official tutorial]: http://tomaka.github.io/glium/book/index.html

#![cfg_attr(feature = "cargo-clippy", allow(forget_copy))]

#[macro_use]
extern crate glium;

use glium::{glutin, Surface};

/// Define a struct to store vertices. This struct will be used by `glium` directly.
#[derive(Copy, Clone)]
struct Vertex {
    position: [f32; 2],
}

implement_vertex!(Vertex, position);

fn main() {
    let mut events_loop = glutin::EventsLoop::new();
    let window = glutin::WindowBuilder::new();
    let context = glutin::ContextBuilder::new();
    let display = glium::Display::new(window, context, &events_loop).unwrap();

    let vertex1 = Vertex {
        position: [0.0, 0.0],
    };
    let vertex2 = Vertex {
        position: [0.5, 0.0],
    };
    let vertex3 = Vertex {
        position: [0.0, 0.5],
    };
    let shape = vec![vertex1, vertex2, vertex3];

    let vertex_buffer = glium::VertexBuffer::new(&display, &shape).unwrap();
    let indices = glium::index::NoIndices(glium::index::PrimitiveType::TrianglesList);

    // Define the shaders.
    let vertex_shader_src = r#"
        #version 140

        in vec2 position;
        out vec2 a_color;

        void main() {
            a_color = position;
            gl_Position = vec4(position, 0.0, 1.0);
        }
    "#;

    let fragment_shader_src = r#"
        #version 140

        in vec2 a_color;
        out vec4 color;

        void main() {
            color = vec4(a_color, 0.0, 1.0);
        }
    "#;

    let program =
        glium::Program::from_source(&display, vertex_shader_src, fragment_shader_src, None)
            .unwrap();

    let draw = || {
        let mut target = display.draw();
        target.clear_color(0.3, 0.3, 0.3, 0.0);
        target
            .draw(
                &vertex_buffer,
                &indices,
                &program,
                &glium::uniforms::EmptyUniforms,
                &Default::default(),
            )
            .unwrap();
        target.finish().unwrap();
    };

    // Finally, draw the triangle!
    draw();

    events_loop.run_forever(|event| {
        if let glutin::Event::WindowEvent { event, .. } = event {
            match event {
                glutin::WindowEvent::CloseRequested => return glutin::ControlFlow::Break,
                glutin::WindowEvent::Resized(..) => draw(),
                _ => (),
            }
        }
        glutin::ControlFlow::Continue
    });
}

use std::fs::File;
use std::io::{BufRead, BufReader};

fn is_ordered(s: &str) -> bool {
    let mut prev = '\x00';
    for c in s.chars() {
        if c < prev {
            return false;
        }
        prev = c;
    }

    true
}

fn find_longest_ordered_words(dict: Vec<String>) -> Vec<String> {
    let mut result = Vec::new();
    let mut longest_length = 0;

    for s in dict {
        if is_ordered(&s) {
            let n = s.len();
            if n > longest_length {
                longest_length = n;
                result.truncate(0);
            }
            if n == longest_length {
                result.push(s.to_owned());
            }
        }
    }

    result
}

fn main() {
    let lines = BufReader::new(File::open("unixdict.txt").unwrap())
        .lines()
        .map(|l| l.unwrap())
        .collect();

    let longest_ordered = find_longest_ordered_words(lines);

    for s in &longest_ordered {
        println!("{}", s.to_string());
    }
}

