fn is_prime(number: i32) -> bool {
    if number % 2 == 0 && number != 2 {
        return false;
    }

    let limit = (number as f32).sqrt() as i32 + 1;

    // We test if the number is divisible by any odd number up to the limit
    (3..limit).step_by(2).all(|x| number % x != 0)
}

