        30, 31, 32, 33, 35, 36, 37, 38, 39,
    ];

    for (i, (lo, hi)) in RangeFinder::new(n).enumerate() {
        if i > 0 {
            print!(", ")
        }
        print!("{}", lo);
        if hi.is_some() {
            print!("-{}", hi.unwrap())
        }
