fn test_rev_words() {
    let tests = [
        ("", ""),
        ("a", "a"),
        ("a b", "b a"),
        ("cat dog", "dog cat"),
        // According to the problem, multiple spaces can be
        // compressed into a single space.
        ("cat     dog", "dog cat"),
        ("cat dog frog", "frog dog cat"),
    ];

    for &(input, expected) in &tests {
        let output = rev_words(input);
        assert_eq!(expected, output);
    }
}

