fn test_coherence() {
    let coherence_test = (50000i32..50050).map(|x| format!("{}", x)).all(|s| {
        let encoded = rot13(&s[..]);
        let decoded = rot13(&encoded[..]);
        decoded == s
    });

    assert!(coherence_test);
}

