//! Straight port from golang crypto/sha1 library implementation
use std::io::{Result, Write};
use std::num::Wrapping as wr;

/// The size of a SHA1 checksum in bytes.
const SIZE: usize = 20;

/// The blocksize of SHA1 in bytes.
const CHUNK: usize = 64;
const INIT: [wr<u32>; 5] = [
    wr(0x6745_2301),
    wr(0xEFCD_AB89),
    wr(0x98BA_DCFE),
    wr(0x1032_5476),
    wr(0xC3D2_E1F0),
];

fn main() {
    let mut d = Digest::new();
    let _ = write!(&mut d, "The quick brown fox jumps over the lazy dog");
    let sha1 = d.sha1();

    for h in &sha1 {
        print!("{:x} ", *h);
    }
}

/// digest represents the partial evaluation of a checksum.
struct Digest {
    h: [wr<u32>; 5],
    x: [u8; CHUNK],
    nx: usize,
    len: u64,
}

impl Digest {
    fn new() -> Digest {
        Digest {
            h: INIT,
            x: [0u8; CHUNK],
            nx: 0,
            len: 0u64,
        }
    }

    fn sha1(&mut self) -> [u8; SIZE] {
        let mut len = self.len;
        // Padding.  Add a 1 bit and 0 bits until 56 bytes mod 64.
        let mut tmp: [u8; 64] = [0u8; 64];
        tmp[0] = 0x80u8;

        let m: usize = (len % 64u64) as usize;
        if m < 56 {
            self.write_all(&tmp[0..56 - m]).unwrap();
        } else {
            self.write_all(&tmp[0..64 + 56 - m]).unwrap();
        }

        // Length in bits (=length in bytes*8=shift 3 bits to the right).
        len <<= 3;
        for (i, byte) in tmp.iter_mut().take(8).enumerate() {
            *byte = (len >> (56 - 8 * i)) as u8;
        }
        self.write_all(&tmp[0..8]).unwrap();

        assert!(self.nx == 0);

        let mut digest: [u8; SIZE] = [0u8; SIZE];
        for (i, s) in self.h.iter().enumerate() {
            digest[i * 4] = (*s >> 24).0 as u8;
            digest[i * 4 + 1] = (*s >> 16).0 as u8;
            digest[i * 4 + 2] = (*s >> 8).0 as u8;
            digest[i * 4 + 3] = s.0 as u8;
        }
        digest
    }

    fn process_block(&self, data: &[u8]) -> [wr<u32>; 5] {
        let k: [u32; 4] = [0x5A82_7999, 0x6ED9_EBA1, 0x8F1B_BCDC, 0xCA62_C1D6];

        #[inline]
        fn part(a: wr<u32>, b: wr<u32>) -> (wr<u32>, wr<u32>) {
            ((a << 5 | a >> (32 - 5)), (b << 30 | b >> (32 - 30)))
        }

        let mut w: [u32; 16] = [0u32; 16];

        let (mut h0, mut h1, mut h2, mut h3, mut h4) =
            (self.h[0], self.h[1], self.h[2], self.h[3], self.h[4]);

        let mut p = data;

        while p.len() >= CHUNK {
            for i in 0..16 {
                let j = i * 4;
                w[i] = u32::from(p[j]) << 24
                    | u32::from(p[j + 1]) << 16
                    | u32::from(p[j + 2]) << 8
                    | u32::from(p[j + 3]);
            }

            let (mut a, mut b, mut c, mut d, mut e) = (h0, h1, h2, h3, h4);

            for i in 0..16 {
                let f = b & c | (!b) & d;
                let (a5, b30) = part(a, b);
                let t = a5 + f + e + wr(w[i & 0xf]) + wr(k[0]);
                b = a;
                a = t;
                e = d;
                d = c;
                c = b30;
            }
            for i in 16..20 {
                let tmp = w[(i - 3) & 0xf] ^ w[(i - 8) & 0xf] ^ w[(i - 14) & 0xf] ^ w[(i) & 0xf];
                w[i & 0xf] = tmp << 1 | tmp >> (32 - 1);
                let f = b & c | (!b) & d;
                let (a5, b30) = part(a, b);
                let t = a5 + f + e + wr(w[i & 0xf]) + wr(k[0]);
                b = a;
                a = t;
                e = d;
                d = c;
                c = b30;
            }
            for i in 20..40 {
                let tmp = w[(i - 3) & 0xf] ^ w[(i - 8) & 0xf] ^ w[(i - 14) & 0xf] ^ w[(i) & 0xf];
                w[i & 0xf] = tmp << 1 | tmp >> (32 - 1);
                let f = b ^ c ^ d;
                let (a5, b30) = part(a, b);
                let t = a5 + f + e + wr(w[i & 0xf]) + wr(k[1]);
                b = a;
                a = t;
                e = d;
                d = c;
                c = b30;
            }
            for i in 40..60 {
                let tmp = w[(i - 3) & 0xf] ^ w[(i - 8) & 0xf] ^ w[(i - 14) & 0xf] ^ w[(i) & 0xf];
                w[i & 0xf] = tmp << 1 | tmp >> (32 - 1);
                let f = ((b | c) & d) | (b & c);
                let (a5, b30) = part(a, b);
                let t = a5 + f + e + wr(w[i & 0xf]) + wr(k[2]);
                b = a;
                a = t;
                e = d;
                d = c;
                c = b30;
            }
            for i in 60..80 {
                let tmp = w[(i - 3) & 0xf] ^ w[(i - 8) & 0xf] ^ w[(i - 14) & 0xf] ^ w[(i) & 0xf];
                w[i & 0xf] = tmp << 1 | tmp >> (32 - 1);
                let f = b ^ c ^ d;
                let (a5, b30) = part(a, b);
                let t = a5 + f + e + wr(w[i & 0xf]) + wr(k[3]);
                b = a;
                a = t;
                e = d;
                d = c;
                c = b30;
            }
            h0 += a;
            h1 += b;
            h2 += c;
            h3 += d;
            h4 += e;

            p = &p[CHUNK..];
        }
        [h0, h1, h2, h3, h4]
    }
}

impl Default for Digest {
    fn default() -> Self {
        Self::new()
    }
}

impl Write for Digest {
    fn write(&mut self, buf: &[u8]) -> Result<usize> {
        self.write_all(buf)?;
        Ok(buf.len())
    }

    #[inline]
    fn write_all(&mut self, buf: &[u8]) -> Result<()> {
        let mut buf_m = buf;

        self.len += buf_m.len() as u64;

        if self.nx > 0 {
            let mut n = buf_m.len();
            if n > CHUNK - self.nx {
                n = CHUNK - self.nx;
            }
            for i in 0..n {
                self.x[self.nx + i] = buf_m[i];
            }
            self.nx += n;
            if self.nx == CHUNK {
                let x = &(self.x[..]);
                self.h = self.process_block(x);
                self.nx = 0;
            }
            buf_m = &buf_m[n..];
        }
        if buf_m.len() >= CHUNK {
            let n = buf_m.len() & !(CHUNK - 1);
            let x = &(self.x[n..]);
            self.h = self.process_block(x);
            buf_m = &buf_m[n..];
        }
        let ln = buf_m.len();
        if ln > 0 {
            assert!(self.x.len() >= ln);
            self.x.as_mut().write_all(buf_m).unwrap();
            self.nx = ln;
        }
        Ok(())
    }

    fn flush(&mut self) -> Result<()> {
        Ok(())
    }
}

#[test]
fn known_sha1s() {
    let input_output = [
        (
            "His money is twice tainted: 'taint yours and 'taint mine.",
            [
                0x59u8, 0x7f, 0x6a, 0x54, 0x0, 0x10, 0xf9, 0x4c, 0x15, 0xd7, 0x18, 0x6, 0xa9, 0x9a,
                0x2c, 0x87, 0x10, 0xe7, 0x47, 0xbd,
            ],
        ),
        (
            "The quick brown fox jumps over the lazy dog",
            [
                0x2fu8, 0xd4, 0xe1, 0xc6, 0x7a, 0x2d, 0x28, 0xfc, 0xed, 0x84, 0x9e, 0xe1, 0xbb,
                0x76, 0xe7, 0x39, 0x1b, 0x93, 0xeb, 0x12,
            ],
        ),
        (
            "The quick brown fox jumps over the lazy cog",
            [
                0xdeu8, 0x9f, 0x2c, 0x7f, 0xd2, 0x5e, 0x1b, 0x3a, 0xfa, 0xd3, 0xe8, 0x5a, 0x0b,
                0xd1, 0x7d, 0x9b, 0x10, 0x0d, 0xb4, 0xb3,
            ],
        ),
    ];

    for &(i, o) in &input_output {
