fn sort_custom(strings: &mut Vec<&str>) {
    use std::cmp::Ordering;
    strings.sort_by(|a, b| {
        if a.len() > b.len() {
            return Ordering::Less;
        }
        if a.len() < b.len() {
            return Ordering::Greater;
        }
        a.cmp(b)
    });
}

