fn comb_sort<T: PartialOrd>(v: &mut [T]) {
    let len = v.len();
    let mut gap: usize = v.len();
    let mut swapped: bool = true;
    while gap > 1 || swapped {
        if gap > 1 {
            gap = (gap as f32 / 1.25) as usize;
        }
        swapped = false;
        for i in 0..len - gap {
            if v[i] > v[i + gap] {
                swapped = true;
                v.swap(i, i + gap);
            }
        }
    }
}

