        assert_eq!(
            "▁▂▃▄▅▆▇█▇▆▅▄▃▂▁",
            sparkline(&read_samples("1 2 3 4 5 6 7 8 7 6 5 4 3 2 1"))
        );
    }

    #[test]
    fn complex() {
        assert_eq!(
            "▂▁▄▃▆▅█▇",
            sparkline(&read_samples("1.5, 0.5 3.5, 2.5 5.5, 4.5 7.5, 6.5"))
        );
    }
}

