    assert_eq!(match_string("abcd", "ab"), (true, true, false));
    assert_eq!(match_string("abcd", "ba"), (false, false, false));
    assert_eq!(match_string("abcd", "bc"), (false, true, false));
    assert_eq!(match_string("abcd", "cd"), (false, true, true));
}

