extern crate time;
use time::{at, get_time, strftime};

fn main() {
    // Prints the current time as a timespec containing the seconds
    // and nanoseconds since 1970-01-01T00:00:00Z.
    let time_ts = get_time();
    println!("seconds: {} nanoseconds: {}", time_ts.sec, time_ts.nsec);

    // Convert the timespec to a broken-down time value Tm
    // Could also use "let time_tm = now();" to get directly
    let time_tm = at(time_ts);

    // Display time formatted according to the asctime format in ISO
    // C, in the local timezone, eg "Wed Oct 29 22:26:17 2014"
    println!("ctime: {}", time_tm.ctime());

    // Display time formatted according to RFC 822,
    // eg "Wed, 29 Oct 2014 22:26:17"
    println!("rfc822: {}", time_tm.rfc822());

    // Display time formatted according to RFC 3339/ISO8601
    // eg "2014-10-29T22:26:17+07:00"
    println!("rfc3339: {}", time_tm.rfc3339());

    // Display time in a custom format (eg "22:26:17") using strftime
    println!("Custom: {}", strftime("%H:%M:%S", &time_tm).unwrap());
}

extern crate chrono;

use std::env;
use std::fs::OpenOptions;
use std::io::prelude::*;
use std::io::{self, BufReader, BufWriter};

const FILENAME: &str = "NOTES.TXT";

fn show_notes() -> Result<(), io::Error> {
    let file = OpenOptions::new()
        .read(true)
        .create(true) // create the file if not found
        .write(true) // necessary to create the file
        .open(FILENAME)?;
    let mut buf_reader = BufReader::new(file);
    let mut contents = String::new();
    buf_reader.read_to_string(&mut contents)?;
    println!("{}", contents);
    Ok(())
}

fn add_to_notes(note: &str) -> Result<(), io::Error> {
    let file = OpenOptions::new()
        .append(true) // disables overwriting, writes to the end of the file
        .create(true)
        .open(FILENAME)?;
    let mut buf_writer = BufWriter::new(file);

    let date_and_time = chrono::Local::now();
    writeln!(buf_writer, "{}", date_and_time)?;

    writeln!(buf_writer, "\t{}", note)
}

fn main() {
    let note = env::args().skip(1).collect::<Vec<_>>();

    if note.is_empty() {
        show_notes().expect("failed to print NOTES.TXT");
    } else {
        add_to_notes(&note.join(" ")).expect("failed to write to NOTES.TXT");
    }
}

// http://rosettacode.org/wiki/Taxicab_numbers

use std::cmp::Ordering;
use std::collections::BinaryHeap;

/// A type to represent a pair-sum of cubes.
/// value = a^3 + b^3
#[derive(Copy, Clone, PartialEq, Eq)]
struct SumCubes {
    a: u64,
    b: u64,
    value: u64,
}

impl SumCubes {
    fn new(a: u64, b: u64) -> SumCubes {
        SumCubes {
            value: a.pow(3) + b.pow(3),
            a: a,
            b: b,
        }
    }
}

impl PartialOrd for SumCubes {
    fn partial_cmp(&self, other: &SumCubes) -> Option<Ordering> {
        // Comparison is reversed to make PriorityQueue behave like a min-heap
        (other.value, other.a, other.b).partial_cmp(&(self.value, self.a, self.b))
    }
}

impl Ord for SumCubes {
    fn cmp(&self, other: &SumCubes) -> Ordering {
        self.partial_cmp(other).unwrap()
    }
}

/// An iterator through all Taxicab numbers
struct TaxicabNumbers {
    /// Use a BinaryHeap as a priority queue to iterate through sums of
    /// cubes efficiently in increasing order
    pq: BinaryHeap<SumCubes>,
}

impl TaxicabNumbers {
    fn new() -> TaxicabNumbers {
        let mut res = TaxicabNumbers {
            pq: BinaryHeap::new(),
        };
        res.pq.push(SumCubes::new(1, 1)); // Start with 1^3 + 1^3
        res
    }
}

impl Default for TaxicabNumbers {
    fn default() -> Self {
        Self::new()
    }
}

impl Iterator for TaxicabNumbers {
    type Item = Vec<SumCubes>;

    fn next(&mut self) -> Option<Vec<SumCubes>> {
        let mut ways = Vec::new(); // All the ways we can express the current
                                   // value as a sum of cubes
        ways.push(SumCubes::new(0, 0)); // Just a sentinel value

        loop {
            let nxt = self.pq.pop().unwrap();
            if nxt.value == ways[0].value {
                // If the next sum is the same as the current one,
                // then we found another way to express the current value
                ways.push(nxt);
            } else if ways.len() > 1 {
                // If we can express the current value in more than one ways,
                // then it's a Taxicab number
                self.pq.push(nxt);
                return Some(ways);
            } else {
                ways.clear();
                ways.push(nxt);
            }

            // Populate the priority queue with more sums
            self.pq.push(SumCubes::new(nxt.a + 1, nxt.b));
            if nxt.a == nxt.b {
                self.pq.push(SumCubes::new(nxt.a + 1, nxt.b + 1));
            }
        }
    }
}

fn main() {
    let numbers = TaxicabNumbers::new();
    for (at, ways) in numbers
        .take(2006)
        .enumerate()
        .filter(|&(at, _)| at + 1 <= 25 || at + 1 >= 2000)
    {
