#![feature(iterator_step_by)]

use std::fmt;

enum Direction {
    RIGHT,
    UP,
    LEFT,
    DOWN,
}
use Direction::*;

fn is_prime(a: u32) -> bool {
    match a {
        2 => true,
        x if x <= 1 || x % 2 == 0 => false,
        _ => {
            let max = f64::from(a).sqrt() as u32;
            for x in (3..max).step_by(2) {
                if a % x == 0 {
                    return false;
                }
            }
